from .RemoteControl import RemoteControl
from .TV import TV


class NumberControl(RemoteControl):
    def __init__(self, tv: TV) -> None:
        super().__init__(tv)
        self._currentNumber = -1
        self._maxNumber = self._tv.getChannelTotal()-1

    def lastChannel(self):
        if self._currentNumber > 1:
            self._currentNumber -= 1
            self._tv.setChannel(self._currentNumber)

    def nextChannel(self):
        if self._currentNumber < self._maxNumber:
            self._currentNumber += 1
            self._tv.setChannel(self._currentNumber)

    def off(self):
        self._tv.off()

    def on(self):
        self._tv.on()

    def switchChannel(self, number: int) -> None:
        if number>0 and number<=self._maxNumber:
            self._currentNumber = number
            self._tv.setChannel(number)
